package com.utkarsh.autoimageslider;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.bumptech.glide.Glide;
import com.smarteist.autoimageslider.SliderViewAdapter;

public class SliderAdapterExample extends SliderViewAdapter<SliderAdapterExample.SliderAdapterVH> {

    private Context context;

    SliderAdapterExample(Context context) {
        this.context = context;
    }

    @Override
    public SliderAdapterVH onCreateViewHolder(ViewGroup parent) {
        View inflate = LayoutInflater.from(parent.getContext()).inflate(R.layout.note_item,parent,false);
        return new SliderAdapterVH(inflate);
    }

    @Override
    public void onBindViewHolder(SliderAdapterVH viewHolder, int position) {

        switch (position) {
            case 0:
                Glide.with(viewHolder.view)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.imageViewBackground);
                break;
            case 1:
                Glide.with(viewHolder.view)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.imageViewBackground);
                break;
            case 2:
                Glide.with(viewHolder.view)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.imageViewBackground);
                break;
            default:
                Glide.with(viewHolder.view)
                        .load("data:image/jpeg;base64,")
                        .into(viewHolder.imageViewBackground);
                break;
        }
    }

    @Override
    public int getCount() {
        return 4;
    }

    class SliderAdapterVH extends SliderViewAdapter.ViewHolder {

        View view;
        ImageView imageViewBackground;

        SliderAdapterVH(View itemView) {
            super(itemView);

            view = itemView;
            imageViewBackground = itemView.findViewById(R.id.iv_auto_image_slider);
        }
    }
}
